<template>
  <div class="asideLeft" :class="showDrawer?'opened':''">
    <div class="wrapper">
      <a href="javascript:;" v-for="(item, index) in navList" :key="index" class="btn-aside" :class="asideType === item.type?'active':''" @click="selectNav(item.type)">
        <i class="fa" :class="item.icon"></i>
        <div class="text">{{item.text}}</div>
      </a>
    </div>
    <div class="container" v-if="showDrawer">
      <div class="font-wrapper" v-if="asideType === 'font'">
        <p class="btn-box">
          <a href="javascript:;" :class="fontType === 1 ? 'active': ''" @click="fontSpecial">特效字</a>
          <a href="javascript:;" :class="fontType === 2 ? 'active': ''" @click="fontGroup">组合字</a>
        </p>
        <div class="con" v-if="fontType === 1">
          <div class="item" @click="selectFont">
            <img src="data:image/png;base64," alt="">
          </div>
          <div class="item">
            <img src="data:image/png;base64," alt="">
          </div>
        </div>
        <div class="con" v-else>
          <div class="item">
            <img src="data:image/jpeg;base64," alt="">
          </div>
        </div>
      </div>
      <div></div>
      <div class="background-wrapper" v-if="asideType === 'background'">
        <el-row>
          <el-col :span="12">
            <div class="item" @click="selectBackground(require('../../../assets/ning1.jpg'))">
              <img src="../../../assets/ning1.jpg" alt="">
            </div>
          </el-col>
          <el-col :span="12">
            <div class="item" @click="selectBackground(require('../../../assets/ning2.jpg'))">
              <img src="../../../assets/ning2.jpg" alt="">
            </div>
          </el-col>
          <el-col :span="12">
            <div class="item" @click="selectBackground(require('../../../assets/ning3.jpg'))">
              <img src="../../../assets/ning3.jpg" alt="">
            </div>
          </el-col>
        </el-row>
      </div>
    </div>
    <a href="javascript:;" class="btn-openAside" @click="showContainer">
      <i class="fa fa-chevron-left"></i>
    </a>
  </div>
</template>
<script>
export default {
  name: 'AsideLeft',
  props: {
    canvasWidth: {
      type: String,
      default: ''
    }
  }
  watch: {
    canvasWidth(val) {
      this.fontParams.width = val
    }
  },
  data() {
    return {
      showDrawer: false,
      navList: [
        {
          type: 'font',
          icon: 'fa-font',
          text: '文本'
        },
        {
          type: 'material',
          icon: 'fa-twitter-square',
          text: '素材'
        },
        {
          type: 'background',
          icon: 'fa-th-large',
          text: '背景'
        },
        {
          type: 'upload',
          icon: 'fa-cloud-upload',
          text: '上传'
        }
      ],
      asideType: 'font', // 菜单选中类型 默认选中font
      fontType: 1, // 文字类型 1特效文字 2组合文字
      fontParams: {
        left: 0,
        top:0,
        text: '执子之手，方知子丑，泪流满面，子不走我走',
        textAlign: 'center',
        width:750,
        fontFamily: 'Microsoft YaHei',
        fontWeight: '400',
        fontSize: '40',
        fill: '#f00'
      }
    }
  },
  methods: {
    selectNav(type) {
      this.asideType = type
    },
    showContainer() {
      // 控制抽屉
      this.showDrawer = !this.showDrawer
    },
    fontSpecial() {
      this.fontType = 1
    },
    fontGroup() {
      this.fontType = 2
    },
    selectFont() {
      this.$emit('setFont', this.fontParams)
    },
    selectBackground(url) {
      this.$emit('setBackgroundImage',url)
    }
  }
}
</script>
<style scoped>
.asideLeft{
  position: fixed;
  left: 0;
  top: 0;
  width:80px;
  height: 100%;
  padding-top:58px;
  text-align: center;
  z-index: 1;
}
.asideLeft.opened{width:360px;}
.asideLeft .wrapper {
  width: 80px;
  height: 100%;
  background: #fff;
  border-right: 1px solid #f1f3f7;
  overflow: auto;
  padding-bottom: 80px;
  box-sizing:border-box;
  -webkit-box-sizing: border-box;
  position: absolute;
  left:0;
  top:58px;
  bottom:0;
}
.asideLeft .wrapper a{
  position: relative;
  display: block;
  text-align: center;
  height: 46px;
  cursor: pointer;
  margin-top: 20px;
  border-left: 4px solid #fff;
  border-right: 4px solid #fff;
  box-sizing:border-box;
  -webkit-box-sizing:border-box;
}
.asideLeft .wrapper a.active, .asideLeft .wrapper a:hover {
  border-left-color: #ff4555;
}
.asideLeft .wrapper a i {
  color: #999;
  font-size: 26px;
  display: inline-block;
  width: 26px;
  height: 26px;
  margin: 0 auto;
}
.asideLeft .wrapper a .text {
  color: #666;
  font-size: 12px;
  margin-top: 4px;
}
.asideLeft .wrapper .active i , .asideLeft .wrapper .active .text, .asideLeft .wrapper a:hover i, .asideLeft .wrapper a:hover .text{color: #ff4555;}
.asideLeft .container{
  width:280px;
  height:100%;
  position: absolute;
  right:0;
  top:58px;
  bottom:0;
  box-shadow: 0 5px 5px #ccc;
  -webkit-box-shadow: 0 5px 5px #ccc;
}
.asideLeft .btn-openAside{
  height: 85px;
  text-align: center;
  line-height: 85px;
  color: #fff;
  width: 20px;
  position: absolute;
  top: 50%;
  right: -20px;
  margin-top: -43px;
  cursor: pointer;
  border-radius: 24px 0 0 24px;
  background: linear-gradient(135deg,#ffa383,#f25b4a);
  -webkit-transform: scaleX(-1);
  transform: scaleX(-1);
}
.asideLeft .btn-box{padding:20px 0;}
.asideLeft .btn-box a{
  display:inline-block;
  padding:8px 20px;
  border: 1px solid #d6dbe1;
  background-color: #fff;
  border-radius: 4px;
  cursor: pointer;
  -webkit-transition: all .28s;
  transition: all .28s;
  margin: 0 5px;
  font-size: 12px;
  color: #202020;
}
.font-wrapper .btn-box a.active{
  color: #fff;
  background: #ff4555;
  border-color: #ff4555;
}
.font-wrapper .con .item{
  width:50%;
  float: left;
  padding: 10px;
  cursor: pointer;
  overflow: hidden;
  text-align: center;
  box-sizing: border-box;
  -webkit-box-sizing: border-box;
}
.font-wrapper .con .item img{
  border: 1px solid #d6dbe1;
  display:block;
  width:100%;
  -webkit-transition: all .2s;
  transition: all .2s;
  -webkit-box-shadow: 0 2px 6px 1px rgba(198,202,209,0);
  box-shadow: 0 2px 6px 1px rgba(198,202,209,0);
  border-radius: 4px;
}
.font-wrapper .con .item img:hover{
  -webkit-box-shadow: 0 0 6px #ccc;
  box-shadow: 0 0 6px #ccc;
}
.background-wrapper{padding:5px;}
.background-wrapper .item{padding:5px;}
.background-wrapper img{display:block;width:100%;}
</style>